export default OfferCard

const Top = styled.div``
const Bottom = styled.div``
const Icon = styled.div``
const IconContainer = styled.div``
const Title = styled.p``
const Url = styled(Link)``
const Claim = styled(Link)``

const Card = styled.div`
background-color: #fff;
box-shadow: 0px 6px 15px -2px rgba(0, 0, 0, 0.2);
flex-basis: 15.3rem;
border-radius: 0.4rem;
margin: 0 1.5rem;
margin-bottom: 2rem;
position: relative;
transition: all linear 260ms;
display: flex;
flex-direction: column;
  ${Url} {
    font-size: 0;
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    visibility: hidden;
    color: transparent;
  }
  ${Top} {
    height: 9rem;
    display: flex;
    flex-direction: column;
    justify-content: center;
    ${IconContainer} {
      align-self: center;
      border-radius: 50%;
      height: 85px;
      width: 85px;
      box-shadow: 0px 6px 15px -2px rgba(0, 0, 0, 0.2);
      display: flex;
      flex-direction: column;
      justify-content: center;
      transition: all linear 260ms;
      ${Icon} {
        align-self: center;
        border-radius: 50%;
        height: 65px;
        width: 65px;
        position: relative;
        display: flex;
        flex-direction: column;
        justify-content: center;
        svg {
          height: 55%;
          width: 55%;
          align-self: center;
          fill: #fff;
        }
      }
    }
  }
  ${Bottom} {
    padding: 1rem 1.5rem;
    padding-top: 0;
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    flex-grow: 1;
    ${Title} {
      text-align: center;
      font-weight: bold;
      margin: unset;
      font-size: 1.2rem;
    }
    ${Claim} {
      align-self: center;
      text-decoration: none;
      padding: 7px 14px;
      border-radius: 3px;
      margin-top: auto;
      margin-top: 1.2rem;
      font-weight: bold;
      font-size: 1rem;
      z-index: 1;
      transition: all linear 260ms;
    }
  }
  &:hover {
    ${Url} {
      visibility: visible;
    }
    ${Top} {
      ${IconContainer} {
        transform: translateY(-4px);
      }
    }
  }
  &:hover,
  &:active,
  &:focus {
    /*box-shadow: 2px 2px 15px 0 rgba(60,60,60,.2);*/
    background-color: #f5f5f5;
  }
  &[type="Discount"] {
    ${Top} {
      ${IconContainer} {
        ${Icon} {
          background-color: #00c99d;
          box-shadow: 0px 6px 15px -2px ${rgba("#00c99d", 0.25)};
        }
      }
    }
    ${Bottom} {
      ${Claim} {
        color: #00c99d;
        background-color: ${rgba("#00c99d", 0.1)};
        &:hover {
          background-color: #00c99d;
          color: #fff;
        }
      }
    }
  }
  &[type="Deal"] {
    ${Top} {
      ${IconContainer} {
        ${Icon} {
          background-color: #ff477d;
          box-shadow: 0px 6px 15px -2px ${rgba("#ff477d", 0.25)};
        }
      }
    }
    ${Bottom} {
      ${Claim} {
        color: #ff477d;
        background-color: ${rgba("#ff477d", 0.1)};
        &:hover {
          background-color: #ff477d;
          color: #fff;
        }
      }
    }
  }
  @media screen and (max-width: 729px) {
    flex-basis: 24rem;
  }
  @media screen and (max-width: 450px) {
    flex-basis: 21rem;
  }
  @media screen and (max-width: 380px) {
    flex-basis: 18rem;
  }
  @media screen and (max-width: 310px) {
    flex-basis: 16rem;
  }
`